private async Task<bool> KickFish()
{
	if (QuestDone(39614)) return false;
	if (_currentFish == null || !_currentFish.IsValid || _currentFish.Z < 620)
	{
		_currentFish = ObjectManager.GetObjectsOfType<WoWUnit>()
			.Where(u => u.Entry == 95148 && u.Z >= 620  && !Blacklist.Contains(u, BlacklistFlags.Interact) && u.DistanceSqr < 60f*60f)
			.OrderBy(u => u.DistanceSqr).FirstOrDefault();

		if (_currentFish == null)
			return false;
	}

	var moveToPoint = WoWMathHelper.CalculatePointFrom(GetBestRiverLocation(), _currentFish.Location, -3);

	if (moveToPoint.DistanceSquared((WoWMovement.ActiveMover ?? StyxWoW.Me).Location) > 1 * 1)
	{
		TreeRoot.StatusText = "Moving to fish";
		return (await CommonCoroutines.MoveTo(moveToPoint)).IsSuccessful();
	}

	TreeRoot.StatusText = "Kicking fish into water";

	await CommonCoroutines.StopMoving();
	await CommonCoroutines.LandAndDismount();
	Me.SetFacing(GetBestRiverLocation());
	await Coroutine.Sleep(200);
	_currentFish.Interact();
	await Coroutine.Sleep(1500);

	return true;
}

private Vector3 GetBestRiverLocation()
{
	return _riverLocations.OrderBy(loc => Vector3Extensions.DistanceSquared(Me.Location, loc)).FirstOrDefault();
}

