private Styx.TreeSharp.Composite _usegoblinSerum;
private WoWItem refinedSerum = StyxWoW.Me.BagItems.FirstOrDefault(x => x.Entry == 115533);
private async Task<bool> UseGoblinSerum()
{
	if (!DoQuest(36428)) return false;
	if (refinedSerum == null || !refinedSerum.IsValid)
	{
		refinedSerum = StyxWoW.Me.BagItems.FirstOrDefault(x => x.Entry == 115533);
		return false;
	}
	if (Me.CurrentTarget == null || Me.CurrentTarget.IsDead || (Me.CurrentTarget.Entry != 81183 && Me.CurrentTarget.Entry != 81184 && Me.CurrentTarget.Entry != 85325)) return false;
	if (Me.CurrentTarget.HealthPercent <= 60) 
		Lua.DoString("PetPassiveMode();");
	if (Me.CurrentTarget.HealthPercent > 35) return false;

	SpellManager.StopCasting();
	refinedSerum.Use();

	await Coroutine.Sleep(1000);
	Lua.DoString("PetAssistMode();");


	return true;
}

While.CustomBehavior:
WoWUnit killMe = null;

async Task VehicleMove(Vector3 moveTo)
{
	while (StyxWoW.Me.InVehicle && Me.Location.Distance(moveTo) >= 5)
	{
		await CombatAwareness();
		Navigator.MoveTo(moveTo);
		await Coroutine.Yield();
	}
}
async Task VehicleKill(uint unitId, Vector3 moveTo)
{
	if (!StyxWoW.Me.InVehicle) return;

	killMe = Unit(unitId, u => u.IsAlive && !Blacklist.Contains(u, BlacklistFlags.Combat));

	if (killMe == null || !killMe.IsValid)
		await VehicleMove(moveTo);

	killMe = Unit(unitId, u => u.IsAlive && !Blacklist.Contains(u, BlacklistFlags.Combat));
	if (killMe == null) return;

	while (StyxWoW.Me.InVehicle && killMe != null && killMe.IsAlive)
	{
		await VehicleMove(killMe.Location);
		await VehicleCombat(killMe);
		await Coroutine.Yield();
	}
}
async Task VehicleCombat(WoWUnit vehicleKill)
{
	if (!StyxWoW.Me.InVehicle) return;
	if (vehicleKill == null) return;
	if (!vehicleKill.IsAlive) return;
	if (vehicleKill.Distance > 5) return;
	if (Me.CurrentTarget != vehicleKill)
		vehicleKill.Target();
	await CommonCoroutines.StopMoving();
	Me.SetFacing(vehicleKill.Location);
	Styx.Helpers.KeyboardManager.KeyUpDown((char)System.Windows.Forms.Keys.D1);
	await Coroutine.Sleep(600);
}
async Task CombatAwareness()
{
	if (!StyxWoW.Me.InVehicle) return;
	var killArray = ObjectManager.GetObjectsOfType<WoWUnit>().Where(u => u.IsTargetingMeOrPet && u.Distance <= 5 && u.IsHostile);
	if (killArray == null) return;
	foreach (WoWUnit killMe in killArray)
	{
		while (StyxWoW.Me.InVehicle && killMe != null && killMe.IsTargetingMeOrPet && killMe.Distance <= 5)
		{
			await VehicleCombat(killMe);
			await Coroutine.Yield();
		}
	}
}
