While.Code:

while (DoQuest(29733) && Me.InVehicle)
{
	WoWUnit hozenPoi = ObjectManager.GetObjectsOfType<WoWUnit>().FirstOrDefault(x => (x.Entry == 55344 || x.Entry == 55454) && x.IsAlive);

	if (hozenPoi != null && hozenPoi.IsValid && hozenPoi.IsAlive)
	{
		hozenPoi.Target();
		Me.SetFacing(hozenPoi.Location);
		Lua.DoString("CastPetAction(3)");
		Lua.DoString("CastPetAction(2)");
		Lua.DoString("CastPetAction(1)");
	}

	await Coroutine.Sleep(1100);
	await Coroutine.Yield();
}

