QuestOrder.Code:

	bool DoQuest(params uint[] questIds)
	{
		foreach (var questId in questIds)
		{
			if (HasQuest(questId) && !IsQuestCompleted(questId)) return true;
		}
		return false;
	}
	bool NeedsQuest(params uint[] questIds)
	{
		foreach (var questId in questIds)
		{
			if (!HasQuest(questId) && !IsQuestCompleted(questId)) return true;
		}
		return false;
	}
	bool NeedsBreadcrumbQuest(params uint[] questIds)
	{
		foreach (var questId in questIds)
		{
			if (IsQuestCompleted(questId)) return false;
		}
		return true;
	}
	bool QuestDone(params uint[] questIds)
	{
		foreach (var questId in questIds)
		{
			if (HasQuest(questId) && IsQuestCompleted(questId)) return true;
		}
		return false;
	}
	bool QuestFlaggedCompleted(params uint[] questIds)
	{
		foreach (var questId in questIds)
		{
			return Lua.GetReturnVal<bool>($"return IsQuestFlaggedCompleted({questId});", 0);
		}
		return false;
	}
	async Task SkipCutscene(int preWait = 3000, int postWait = 3000)
	{
// Add detection of cutscene here.
		await Coroutine.Sleep(preWait);
// Test and improve the working method.
		Lua.DoString("MovieFrame:StopMovie(); CinematicFrame_CancelCinematic(); StopCinematic();");
		await Coroutine.Sleep(postWait);
	}

While.Code:

while (DoQuest(29733) && Me.InVehicle)
{
	WoWUnit hozenPoi = ObjectManager.GetObjectsOfType<WoWUnit>().FirstOrDefault(x => (x.Entry == 55344 || x.Entry == 55454) && x.IsAlive);

	if (hozenPoi != null && hozenPoi.IsValid && hozenPoi.IsAlive)
	{
		hozenPoi.Target();
		Me.SetFacing(hozenPoi.Location);
		Lua.DoString("CastPetAction(3)");
		Lua.DoString("CastPetAction(2)");
		Lua.DoString("CastPetAction(1)");
	}

	await Coroutine.Sleep(1100);
	await Coroutine.Yield();
}

