While.Code:

while (DoQuest(29727) && Me.InVehicle)
{
	WoWUnit hozenPoi = ObjectManager.GetObjectsOfType<WoWUnit>().FirstOrDefault(x => (x.Entry == 55485 || x.Entry == 55411 || x.Entry == 55484 || x.Entry == 55410 || x.Entry == 55473) && x.IsAlive);

	if (hozenPoi != null && hozenPoi.IsValid && hozenPoi.IsAlive)
		hozenPoi.Interact();

	await Coroutine.Sleep(750);
	await Coroutine.Yield();
}


