QuestOrder.CustomBehavior:

	bool DoQuest(params uint[] questIds)
	{
		foreach (var questId in questIds)
		{
			if (HasQuest(questId) && !IsQuestCompleted(questId)) return true;
		}
		return false;
	}
	bool NeedsQuest(params uint[] questIds)
	{
		foreach (var questId in questIds)
		{
			if (!HasQuest(questId) && !IsQuestCompleted(questId)) return true;
		}
		return false;
	}
	bool NeedsBreadcrumbQuest(params uint[] questIds)
	{
		foreach (var questId in questIds)
		{
			if (IsQuestCompleted(questId)) return false;
		}
		return true;
	}
	bool QuestDone(params uint[] questIds)
	{
		foreach (var questId in questIds)
		{
			if (HasQuest(questId) && IsQuestCompleted(questId)) return true;
		}
		return false;
	}
	bool QuestFlaggedCompleted(params uint[] questIds)
	{
		foreach (var questId in questIds)
		{
			return Lua.GetReturnVal<bool>($"return IsQuestFlaggedCompleted({questId});", 0);
		}
		return false;
	}
	async Task SkipCutscene(int preWait = 3000, int postWait = 3000)
	{
// Add detection of cutscene here.
		await Coroutine.Sleep(preWait);
// Test and improve the working method.
		Lua.DoString("MovieFrame:StopMovie(); CinematicFrame_CancelCinematic(); StopCinematic();");
		await Coroutine.Sleep(postWait);
	}


IEnumerable<WoWItem> GetExtraConsumables()
{
	uint foodCount = 0;
	uint drinkCount = 0;
	// Skip conjured and buff comsumables.

	var extraConsumables = (from item in Me.BagItems
					   where item != null && item.ItemInfo != null
					   let isFood = Consumable.IsFood(item.ItemInfo)
					   let isDrink = Consumable.IsDrink(item.ItemInfo)
					   where (isFood || isDrink) && item.ItemInfo.SellPrice > 0 && item.Effects.Count == 1 && item.ItemInfo.RequiredLevel < Me.Level - 10                    
					   select item).ToList();


	var extraPotions = Me.BagItems
		.Where(c => c != null && c.IsValid && c.ItemInfo != null && c.ItemInfo.SellPrice > 0 && c.Effects.Count == 1
			&& (c.ItemInfo.ConsumableClass == WoWItemConsumableClass.Potion
			|| c.ItemInfo.ConsumableClass == WoWItemConsumableClass.Elixir
			|| c.ItemInfo.ConsumableClass == WoWItemConsumableClass.Flask)
			&& c.ItemInfo.RequiredLevel < Me.Level - 20);

	extraConsumables.AddRange(extraPotions);
	return extraConsumables;
}

async Task SellExtraConsumables()
{
	Logging.Write("Selling items in GetExtraConsumables()");
	foreach (var item in GetExtraConsumables())
	{
		Logging.Write("Selling {0}.", item.Name);
		item.UseContainerItem();
		await Coroutine.Sleep(1337);
	}
}

While.CustomBehavior:

private WoWUnit _baaronGeddon;
private WoWItem _flamestaffItem;
private Styx.TreeSharp.Composite _baaronGeddonHook;
private async Task<bool> BaaronGeddonHook()
{
	if (Me.HasAura(92924) && Me.Combat)
	{
		WoWMovement.ClickToMove(new Vector3(5389.462f, -2825.282f, 1517.21f));
		return true;
	}
	if (QuestDone(25464))
		return false;
	if (Me.IsDead)
		return false;
	if (_baaronGeddon == null || !_baaronGeddon.IsValid)
	{
		_baaronGeddon = ObjectManager.GetObjectsOfType<WoWUnit>().FirstOrDefault(u => u.Entry == 40147);
		return false;
	}
	if (_flamestaffItem == null || !_flamestaffItem.IsValid)
	{
		_flamestaffItem = StyxWoW.Me.BagItems.FirstOrDefault(x => x.Entry == 54463);
		return false;
	}
	if (_baaronGeddon.HasAura(74813))
	{
		return (await CommonCoroutines.MoveTo(new Vector3(5410.585f, -2807.549f, 1515.635f))).IsSuccessful();
		return false;
	}

	if (_baaronGeddon.Location.DistanceSquared((WoWMovement.ActiveMover ?? StyxWoW.Me).Location) > 19f*19f)
	{
		TreeRoot.StatusText = "Moving to Baaron Geddon";
		return (await CommonCoroutines.MoveTo(new Vector3(5418.382f, -2805.072f, 1516.245f))).IsSuccessful();
	}

	if (!Me.IsCasting)
	{
		_baaronGeddon.Target();
		TreeRoot.StatusText = "Using staff on Baaron Geddon.";
		await CommonCoroutines.StopMoving();
		Me.SetFacing(_baaronGeddon.Location);
		_flamestaffItem.Use();
		return true;
	}

	if (Me.IsCasting) return true;

	return false;
}

