WoWUnit thehammerPoi = ObjectManager.GetObjectsOfType<WoWUnit>().FirstOrDefault(x => x.Entry == 36682 && x.IsAlive);
while (DoQuest(24817) && Me.InVehicle && thehammerPoi != null)
{
	if (Me.CurrentTarget != thehammerPoi)
		thehammerPoi.Target();

	if (thehammerPoi.DistanceSqr > 49f*49f)
		WoWMovement.ClickToMove(thehammerPoi.Location);

	if (thehammerPoi.DistanceSqr <= 49f*49f)
	{
		Me.SetFacing(thehammerPoi);
		await CommonCoroutines.StopMoving();

		if (thehammerPoi != null && Lua.GetReturnVal<bool>("return GetPetActionCooldown(1) == 0;", 0))
			Lua.DoString("CastPetAction(1);");

		if (thehammerPoi != null && Lua.GetReturnVal<bool>("return GetPetActionCooldown(2) == 0;", 0))
			Lua.DoString("CastPetAction(2);");

		if (thehammerPoi != null && Lua.GetReturnVal<bool>("return GetPetActionCooldown(3) == 0;", 0))
			Lua.DoString("CastPetAction(3);");
	}

	await Coroutine.Yield();
}
